<?php
// src/Top10/CabinetBundle/Entity/Repository/CommentsRepository.php

namespace Top10\CabinetBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * CommentRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class homesRepository extends EntityRepository
{


//for Posts
    public function getHomes( $arrWhere = null )
    {
		$qb = $this->createQueryBuilder('h');
		$qb->addSelect('h' )
			->leftJoin('h.rooms', 'r', 'WITH', 'r.approved = 1')
			->andWhere('h.approved = 1')
			->addOrderBy('h.created', 'ASC');


		if( isset( $arrWhere['author'] ) )
			$qb->leftJoin('h.author', 'a', 'WITH', 'a.enabled = 1')
				->andWhere( 'a.id = :author' )
					->setParameter( 'author', $arrWhere['author'] );

        return $qb->getQuery()
                  ->getResult();
    }


}
